CREATE TABLE products (
  ID int(11) NOT NULL AUTO_INCREMENT,
  NAME varchar(128) NOT NULL,
  SDESCR varchar(256) NOT NULL,
  DESCR text NOT NULL,
  PICT varchar(32) NOT NULL,
  PRICE float NOT NULL,
  PRIMARY KEY (ID)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;


INSERT INTO products (NAME,SDESCR,DESCR,PICT,PRICE) VALUES
   ('Часы Hanowa','HANOWA - это решение для тех, кто ценит качество и стиль в наручных часах.','HANOWA - это известный бренд ручных часов, отличающийся высоким качеством, точностью и стильным дизайном. Они предлагают широкий ассортимент моделей, включая аналоговые и цифровые часы, а также хронографы для профессиональных спортсменов и любителей активного образа жизни. Материалы корпуса могут быть разнообразными - от нержавеющей стали до керамики и титана, что обеспечивает прочность и долговечность часов. Ремешки также изготавливаются из различных материалов, таких как кожа, ткань или силикон, чтобы обеспечить максимальный комфорт при ношении. Отделочные детали, такие как сапфировые стекла или покрытие PVD, придают часам HANOWA элегантный и роскошный вид. Благодаря своей надежности и точности, HANOWA является отличным выбором для тех, кто ценит качество и стиль в наручных часах.','watch.png',22200.0),
   ('IP-камера MUI','IP-камеры MUI являются отличным выбором для систем видеонаблюдения','MUI - это производитель IP-камер, который предлагает широкий ассортимент устройств для видеонаблюдения. Камеры MUI отличаются высоким качеством изображения, надежностью и простотой использования. Они поддерживают различные форматы видео, включая Full HD и 4K, а также имеют различные функции, такие как ночное видение, детектор движения и удаленный доступ через интернет. Кроме того, MUI предоставляет своим клиентам возможность настройки параметров камеры, а также облачное хранение записей, что позволяет сохранять данные в безопасности и легко получать к ним доступ. В целом, IP-камеры MUI обеспечивают высокое качество изображения при доступной цене.','ipcamera.png',2890.0),
   ('Фотоаппарат Sony','Фотоаппараты Sony пользуются популярностью среди фотографов-любителей и профессионалов','Sony - известный японский производитель электроники, в том числе и фотоаппаратов. Фотоаппараты Sony отличаются высоким качеством изображения, инновационными технологиями и стильным дизайном. В ассортименте компании представлены как любительские, так и профессиональные модели, которые подходят для различных видов съемки. Кроме того, фотоаппараты Sony обладают множеством функций, таких как автоматическая фокусировка, стабилизация изображения, возможность записи видео в высоком разрешении и другие.','phcamera.png',32560.0),
   ('FM-приёмник M-U35','Этот FM-приемник может стать отличным выбором для любителей музыки и новостей','M-U35 - это FM-радиоприемник, который предназначен для приема радиостанций в диапазоне FM. Оснащен функциями автоматического поиска и сохранения станций, а также позволяет регулировать громкость и настраивать частоту. Он имеет компактный размер и легкий вес, что позволяет носить его с собой. Устройство оснащено встроенной антенной, которая обеспечивает качественный прием радиосигналов. M-U35 также имеет функции автоматического поиска и сохранения станций, а также возможность регулировки громкости. ','fmradio.png',1125.0),
   ('Наушники беспроводные Xaomi','Звук, который вам нужен. Интеллектуальное шумоподавление','Возможность подключения к двум устройствам. Беспроводная зарядка. До 28 часов работы от аккумулятора. Гибридное активное шумоподавление флагманского уровня. В наушниках используется технология двойного активного шумоподавления с двумя микрофонами: один отслеживает внешний шум, а второй применяется для обратной связи. Они эффективно подавляют внешний шум и уменьшают избыточный шум в ушном канале. Глубина шумоподавления может достигать 35 дБ. Передвигаетесь ли вы на метро или летите на самолете, шумоподавление эффективно подавляет рев двигателей и другие вибрации и шумы внутри вагона/салона, помогая сделать ваше путешествие тихим и спокойным.','hphones.png',3890.0);